self:Philosopher
    a sh:NodeShape ;

    sh:targetClass dbo:Philosopher ;

    sh:property [
        sh:path rdfs:label ;
        sh:datatype rdf:langString ; 
        sh:languageIn ( "en" ) ;
        sh:minCount 1 ;
        sh:maxCount 1 ;
    ] ;

    sh:property [
        sh:path dbo:thumbnail ;
        shm:alias dbp:thumbnail ;
        sh:minCount 1 ;
        sh:maxCount 1 ;
    ] ;

    sh:property [
        sh:path dbo:birthPlace ;
        sh:nodeKind sh:IRI ;
        sh:node self:Location ;
    ] ;

    sh:property [
        sh:path dbo:birthDate ;
        sh:datatype xsd:date;
        sh:maxCount 1 ;
    ] .

self:Location
    a sh:NodeShape ;

    sh:targetClass <http://dbpedia.org/ontology/Location> ;

    sh:property [
        sh:path rdfs:label ;
        sh:datatype rdf:langString ;
        sh:languageIn ( "en" ) ;
        sh:minCount 1 ;
        sh:maxCount 1 ;
    ] .

